###### applythiscallbind
```javascript
function test(a, b) {
 console.log(a);
 console.log(b);
}
const array = [1, 10];
test(array);
// [1, 10]
// undefined

test.apply(null, array);
// 1
// 10

var test = {
 func: function (a, b) {
  console.log(a);
  console.log(b);
 }
}
const array = [1, 10];
test.func(array);
// [1, 10]
// undefined
test.func.apply(test.func, array);
test.func.apply(null, array);
// 1
// 10

const array = [1, 2, 4, 6, 2, 8];
Math.max(array);
// NaN
const array = [1, 2, 4, 6, 2, 8];
Math.max.apply(null, array);
// 8
```

###### ES6
```javascript
function test(a, b) {
 console.log(a);
 console.log(b);
}
const array = [1, 10];
test(...array);
// 1
// 10

var test = {
 func: function (a, b) {
  console.log(a);
  console.log(b);
 }
}
const array = [1, 10];
test.func(...array);
// 1
// 10

const array = [1, 2, 4, 6, 2, 8];
Math.max(...array);
// 8
```

###### arguments
arguments
```javascript
function test() {
 for (let i = 0; i < arguments.length; i++) {
  console.log(arguments[i]);
 }
}
test(1,12,5);
// 1 12 5
test(1,12,3,4,5);
// 1 12 3 4 5
```

