        if (this.count === 1) { // {2} 
          this.tail = undefined; 
        } else { 
          this.head.prev = undefined; // {3} 
        } 
      } else if (index === this.count - 1) { // 最后一项 //新增的
        current = this.tail; // {4} 
        this.tail = current.prev; // {5} 
        this.tail.next = undefined; // {6} 
      } else { 
        current = this.getElementAt(index); // {7} 
        const previous = current.prev; // {8} 
